function initCategoryHover() {
	var activeClass = 'cover-bg';
	
	jQuery('.js-masonry-block').each(function(){
		var holder = jQuery(this);
		var categoryItems = holder.find('.category-item');
		
		categoryItems.each(function(){
			var categoryItem = jQuery(this);
			var categoryBlocks = holder.find('.grid-item[data-category="' + categoryItem.attr('data-category') + '"]');
			
			categoryBlocks.on('mouseenter touchstart pointerdown MSPointerDown', hoverOnHandler);
			categoryBlocks.on('mouseleave touchend pointerup MSPointerUp', hoverOffHandler);
			
			function hoverOnHandler(){
				categoryItem.find('>.item-holder').addClass(activeClass);
			}
			function hoverOffHandler(){
				categoryItem.find('>.item-holder').removeClass(activeClass);
			}
		})
	})
}

// responsive layout handling
function initLayoutFix() {
	// find elements
	var holders = jQuery('.js-masonry-block:has([data-mobile-index])');
	var resizeTimer;
	var inited;
	var win = jQuery(window);

	function prepareBlocks() {
		holders.each(function() {
			var holder = jQuery(this);
			var mobileBlocks = holder.find('[data-mobile-index]');
			var desktopBlocks = holder.find('[data-mobile-index]');

			desktopBlocks.each(function() {
				var block = jQuery(this);
				var prevBlock = block.prev();
				block.data('prevBlock', prevBlock);
			});

			mobileBlocks.sort(function(a, b) {
				return jQuery(a).data('mobileIndex') - jQuery(b).data('mobileIndex');
			});

			holder.data('mobileBlocks', mobileBlocks);
			holder.data('desktopBlocks', desktopBlocks);
		});
	}

	function initDesktop() {
		holders.each(function() {
			var holder = jQuery(this);
			var desktopBlocks = holder.data('desktopBlocks');
			desktopBlocks.slice(1).each(function() {
				var block = jQuery(this);
				var prevBlock = block.data('prevBlock');
				block.insertAfter(prevBlock);
			});
		});
	}
	function initMobile() {
		holders.each(function() {
			var holder = jQuery(this);
			var mobileBlocks = holder.data('mobileBlocks');
			var appendBlocks = mobileBlocks.slice(1);
			appendBlocks.insertAfter(mobileBlocks.eq(0));
		});
	}

	prepareBlocks();

	// handle layout resize
	ResponsiveHelper.addRange({
		'..1023': {
			on: function() {
				clearTimeout(resizeTimer);
				resizeTimer = setTimeout(function() {
					inited = true;
					initMobile();
					holders.isotope('reloadItems').isotope();
					win.trigger('refreshVideoSize');
				}, 100);
			}
		},
		'1024..': {
			on: function() {
				clearTimeout(resizeTimer);
				resizeTimer = setTimeout(function() {
					if (!inited) return;

					initDesktop();
					holders.isotope('reloadItems').isotope();
					win.trigger('refreshVideoSize');
				}, 100);
			}
		}
	});
}

// next item init
function initNextItem() {
	jQuery('[data-item-group]').each(function() {
		var item = jQuery(this);
		var group;
		if (!item.hasClass('inited')) {
			itemsGroup = jQuery('[data-item-group="' + item.data('itemGroup') + '"]').addClass('inited');
			itemsGroup.nextItem({
				activeClass: 'active',
				btnNext: 'a.icon-arrow[href^="#"]:not([href="#"])',
				onInit: function(self) {
					jQuery('a.js-activate-group').on('click', function(e) {
						var link = jQuery(this);
						var block = jQuery(link.attr('href'));
						if (block.length) {
							e.preventDefault();
							self.activateItem(block);
						}
					});
				}
			});
		}
	});
}

// add classes on hover/touch
function initCustomHover() {
	// detect device type
	var isTouchDevice = /Windows Phone/.test(navigator.userAgent) || ('ontouchstart' in window) || window.DocumentTouch && document instanceof DocumentTouch;

	jQuery('.grid-item .item-holder').touchHover({
		onHover: function(item) {
			if (!isTouchDevice) {
				item.addClass('video-visible');
				item.trigger('play.VideoHTML5');
			}
		},
		onLeave: function(item) {
			if (!isTouchDevice) {
				item.trigger('pause.VideoHTML5');
			}
		}
	});
}

// resize video init
function initResizeVideo() {
	jQuery('.video-block .video-holder:has(video)').each(function() {
		VideoStretcher.add({
			container: this,
			video: 'video'
		});
	});
}

// video block init
