// mobile menu init
function initMobileNav() {
	jQuery('body').mobileNav({
		menuActiveClass: 'nav-active',
		menuOpener: '.nav-opener'
	});
}

// masonry init
function initMasonry() {
	var win = jQuery(window);
	jQuery('.js-masonry-block').isotope({
		layoutMode: 'packery',
		itemSelector: '.grid-item'
	}).each(function() {
		var holder = jQuery(this);
		var items = holder.find('.grid-item:not(.double)');
		var doubleItems = holder.find('.grid-item.double');
		var refreshTimer;

		refreshSizes();
		win.on('load resize orientationchange', resizeHandler);

		function resizeHandler() {
			clearTimeout(refreshTimer);
			refreshTimer = setTimeout(refreshSizes, 50);
		}

		function refreshSizes() {
			var itemWidth;
			items.css({ width: '' });

			itemWidth = Math.floor(items.eq(0).innerWidth() - 1);
			items.css({ width: itemWidth });
			doubleItems.css({ width: 2 * itemWidth });

			holder.isotope('layout');
		}
	});
}

// fancybox modal popup init
function initLightbox() {
	jQuery('a.lightbox, a[rel*="lightbox"]').fancybox({
		helpers: {
			overlay: {
				css: {
					background: 'rgba(50, 50, 50, 0.5)'
				}
			}
		},
		afterLoad: function(current, previous) {
			// handle custom close button in inline modal
			if(current.href.indexOf('#') === 0) {
				jQuery(current.href).find('a.close').off('click.fb').on('click.fb', function(e){
					e.preventDefault();
					jQuery.fancybox.close();
				});
			}
		},
		padding: 2
	});
}


